import React from "react"

import Login from "./Login"
import Buttons from "./Buttons"
import Main from "./Main"
import Registracija from "./Registracija"

import $ from "jquery" 
import _decodeJWT from 'jwt-decode'

class Bioskop extends React.Component{
    constructor(){
        super()
        this.state = {
            usernameRequest:"",
            passwordRequest:"",
            logedUsername:"",
            roles:"",
            NoviFilm: false,
            Users: false,
            filmovi:[]
        }
        this.handleClick=this.handleClick.bind(this)
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
        this.handleToUpdateAdd=this.handleToUpdateAdd.bind(this)
        this.handleToUpdateDelete=this.handleToUpdateDelete.bind(this)
    }
    
    componentDidMount(){
        $('#Registracija').hide()
    }

    handleClick(event){
        $('#main').children().hide();
        const name = event.target.name
        console.log("HandleClick: "+name)
      /*  $('#Filmovi').hide()
        $('#noviFilm').hide()
        $('#NoviFilm').show()*/
        if(name === "logout"){
            localStorage.removeItem('jwt');
            this.setState({
                logedUsername:"",
                roles:"",
                NoviFilm: false,
                Users: false
            })
            $('#idLogin').show()
        }else if(name === "Registracija"){
            $('#idLogin').hide()
            $('#Registracija').show()
            $('#registracija').hide()
        }else if(name === "Filmovi"){
            const config={
                headers: new Headers({'Authorization': 'Bearer ' + localStorage.getItem('jwt')},
                                      {'Access-Control-Allow-Methods':'GET'}),
              };
            fetch('http://localhost:8080/bioskop/filmovi',config)
                .then(res => res.json())
                .then(response => {
                    this.setState({
                        filmovi: response
                })
            })
        }else{
            this.setState({[name]:true})
            $('#'+name).show()
        }
    }

    handleChange(event){
        const {name,value} = event.target
        this.setState({[name]: value})
    }

    handleToUpdateAdd(someArg){
        this.setState(prevState => {
            const updateFilmovi=prevState.filmovi.push(someArg)
            return {
                filmovi: updateFilmovi
            }
        })
    }

    handleToUpdateDelete(someArg){
        this.setState({filmovi:someArg})
    }

    handleSubmit(e){
        e.preventDefault();
        const userLogin={
            username:this.state.usernameRequest,
            password:this.state.passwordRequest
        }
        var config={
            dataType: 'json',
            method: 'post',
            body: JSON.stringify(userLogin)
          };

        fetch('http://localhost:8080/auth/login',config)
          .then(res => res.json())
          .then(res => {
            const jwt = res.accessToken;
            localStorage.setItem('jwt', jwt);
            const decodedJWTData = _decodeJWT(jwt);
	    	if (decodedJWTData != null) {
                this.setState({
                    logedUsername:decodedJWTData.sub,
                    roles:decodedJWTData.roles,
                    passwordRequest:"",
                    usernameRequest:""
                })
	    	}
          })
        $('#idLogin').hide()
        
    }

    render(){
        const buttons=this.state.logedUsername !== "" &&  <Buttons state={this.state} handleClick={this.handleClick}/>
        const main=this.state.logedUsername !== "" && <Main state={this.state} handleToUpdateDelete={this.handleToUpdateDelete} handleToUpdateDelete={this.handleToUpdateDelete}/>
        return(
        <div id="Bioskop">
            <Login handleClick={this.handleClick} handleSubmit={this.handleSubmit} handleChange={this.handleChange} state={this.state}/>
            <Registracija/>
            {buttons}
            <br/>
            {main}
        </div>
    )}
}

export default Bioskop
