import React from "react"

import PrikazKorisnika from "./PrikazKorisnika"

class Users extends React.Component{

    constructor(){
        super()
        this.state = {
            users: [],
            username:"",
            role:""
        }
        this.handleChange=this.handleChange.bind(this)
        this.filter=this.filter.bind(this);
    }

    componentDidMount(){
        fetch('http://localhost:8080/bioskop/users')
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState(prevState => {
            const updateUsers=prevState.users.map(u => {
                if(u.korisnickoIme === name){
                    u.uloga=value
                    var config={
                        dataType: 'json',
                        method: 'put',
                        body: JSON.stringify(u)
                      };
            
                    // On submit of the form, send a POST request with the data to the server.
                    fetch('http://localhost:8080/bioskop/users',config)
                        .then(res => res.json())
                        .then(res => console.log(res))
                }
                return u;
            })
            return {
                users: updateUsers
            }
        })
    }

    filter(event){
        console.log(this.state)
        const {name,value}=event.target
        console.log(name+" "+value)
        this.setState({[name]: value})
        const username=this.state.username === "" && "null";
        const role=this.state.role === "" && "null";
        /**/
    }

    render(){
        const prikaz = this.state.users.map(u => <PrikazKorisnika handleChange={this.handleChange} key={u.korisnickoIme} user={u} />)
        return(
            <div id="Users">
                <table border="1">
                    <thead>
                        <tr>
                            <td>
                                <input 
                                    value={this.state.username} 
                                    type="text" 
                                    name="username" 
                                    placeholder="Korisnicko ime..." 
                                    onChange={this.filter}/></td>
                            <td></td>
                            <td>
                                <select name="role" onChange={this.filter} value={this.state.role}>
                                    <option value="null">--Izaberite ulogu za pretragu--</option>
                                    <option value="user">user</option>
                                    <option value="admin">admin</option>
                                </select>
                            </td>
                        </tr>
                        <tr>
                            <th>Korisnicko ime</th>
                            <th>Datum registracije</th>
                            <th>Uloga</th>
                        </tr>
                    </thead>
                    {prikaz}
                </table>
                <h1>{this.state.username} {this.state.role}</h1>
            </div>
        )
    }
}

function filterUser(props){

    fetch('http://localhost:8080/bioskop/users/'+props.username+"/"+props.role)
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })
}

export default Users
