import React from "react"
import PrikazIzvestaja from "./PrikazIzvestaja"
import ZaglavljeIzvestaja from "./ZaglavljeIzvestaja"

class Izvestaji extends React.Component{
    constructor(){
        super()
        this.state = {
            izvestaji:[],
            pocetak:"",
            kraj:"",
            sort:""
        }
        this.handleChange=this.handleChange.bind(this)
        this.handleClick=this.handleClick.bind(this)
        this.ukupnoProjekcija=this.ukupnoProjekcija.bind(this)
    }

    handleClick(){
        console.log("Handle click")
        const config={
            headers: new Headers({'Authorization': 'Bearer ' + localStorage.getItem('jwt')},
                                  {'Access-Control-Allow-Methods':'GET'}),
          };
        
        const vremeOd=this.state.pocetak === "" ? "null":this.state.pocetak;
        const vremeDo=this.state.kraj === "" ? "null":this.state.kraj;
        const sort=this.state.sort === "" ? "null":this.state.sort;

        fetch('http://localhost:8080/bioskop/izvestaji/'+vremeOd+"/"+vremeDo+"/"+sort,config)
        .then(res => res.json())
        .then(res => {
            console.log(res)
            this.setState({
                izvestaji: res
            })
        })
    }

    handleChange(event){
        const {name,value} = event.target
        this.setState({
            [name]:value
        })
    }

    ukupnoProjekcija(){
        var ukupnoProjekcija=0;
        ukupnoProjekcija=this.state.izvestaji.map(i => parseInt(i.brojProjekcija)+parseInt(ukupnoProjekcija))
        return ukupnoProjekcija
    }

    render(){
        const prikazIzvestaja = this.state.izvestaji.length > 0 && <PrikazIzvestaja state={this.state} />
        return(
            <div id="izvestaji">
                <ZaglavljeIzvestaja ukupnpProjekcija={this.ukupnoProjekcija} handleChange={this.handleChange} handleClick={this.handleClick} />
                {prikazIzvestaja}
            </div>
        )
    }
}

export default Izvestaji
