import React from "react"

class NoviFilm extends React.Component{
    constructor(){
        super()
        this.state = {
            naziv: "",
            reziser: "",
            glumci: "",
            zanrovi: "",
            trajanje: "",
            distributer: "",
            zemljaPorekla: "",
            godinaProizvodnje: "",
            opis: ""
        }
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState({[name]:value})
    } 

    handleSubmit(e){
        e.preventDefault();
        const headers = new Headers();
        headers.append('Access-Request-Method', 'POST');
        headers.append('Content-Type', 'application/json');
        headers.append('Origin','http://localhost:8080/bioskop/filmovi');

        const data = {
            'Accept': 'application/json',
            'Access-Request-Method': 'POST',
            'Content-Type': 'application/json',
            'Origin':'http://localhost:8080/bioskop/filmovi'
        }

        var config={
            mode: "no-cors",
            method: 'post',
            header: data,
            body: JSON.stringify(this.state)
          };

        const request = new Request('http://localhost:8080/bioskop/filmovi/add')

        // On submit of the form, send a POST request with the data to the server.
        fetch('http://localhost:8080/bioskop/filmovi',config)
            .then(res => res.json())
      }

    render(){
        return(
            <div id="NoviFilm">
                <form onSubmit={this.handleSubmit}>
                    <input placeholder="Naziv filma..." name="naziv" type="text" onChange={this.handleChange} value={this.state.naziv} />
                    <br/>
                    <input placeholder="Reziser filma..." name="reziser" type="text" onChange={this.handleChange} value={this.state.reziser} />
                    <br/>
                    <input placeholder="Glumci filma..." name="glumci" type="text" onChange={this.handleChange} value={this.state.glumci} />
                    <br/>
                    <input placeholder="Zanr filma..." name="zanrovi" type="text" onChange={this.handleChange} value={this.state.zanrovi} />
                    <br/>
                    <input placeholder="Trajanje filma..." name="trajanje" type="text" onChange={this.handleChange} value={this.state.trajanje} />
                    <br/>
                    <input placeholder="Distributer filma..." name="distributer" type="text" onChange={this.handleChange} value={this.state.distributer} />
                    <br/>
                    <input placeholder="Zemlja porekla filma..." name="zemljaPorekla" type="text" onChange={this.handleChange} value={this.state.zemljaPorekla} />
                    <br/>
                    <input placeholder="Godina proizvodnje filma..." name="godinaProizvodnje" type="text" onChange={this.handleChange} value={this.state.godinaProizvodnje} />
                    <br/>
                    <input placeholder="Opis filma..." name="opis" type="text" onChange={this.handleChange} value={this.state.opis} />
                    <br/>
                    <button type="submit">Dodaj film</button>
                </form>
                <h1>{this.state.naziv} {this.state.reziser} {this.state.glumci} {this.state.zanrovi} {this.state.trajanje} {this.state.distributer} {this.state.zemljaPorekla} {this.state.godinaProizvodnje} {this.state.opis}</h1>
            </div>
        )
    }

}

export default NoviFilm
