import React, {Component} from "react"

import FilterFilmova from "./FilterFilmova"
import PrikazFilmova from "./PrikazFilmova"
import Film from "./Film"
import EditFilm from "./EditFilm"

import $ from "jquery"

class Filmovi extends Component{
    
    constructor(){
        super()
        this.state = {
            filmovi: [],
            film:"",
            editFilm:""
        }
        this.filter=this.filter.bind(this)
        this.handleClick=this.handleClick.bind(this)
        this.deleteFilm=this.deleteFilm.bind(this)
        this.editFilm=this.editFilm.bind(this)
        this.handleChange=this.handleChange.bind(this)
    }
    
    componentDidMount(){
        fetch('http://localhost:8080/bioskop/filmovi')
            .then(res => res.json())
            .then(response => {
                this.setState({
                    filmovi: response
            })
        })
    }

    filter(){
        const naziv=$('#idNaziv').val() === "" ? "null":$('#idNaziv').val();
        const zanr=$('#idZanr').val() === "" ? "null":$('#idZanr').val();
        const idOd=$('#idOd').val() === "" ? "null":$('#idOd').val();
        const idDo=$('#idDo').val() === "" ? "null":$('#idDo').val();
        const dist=$('#idDist').val() === "" ? "null":$('#idDist').val();
        const zemlja=$('#idZemlja').val() === "" ? "null":$('#idZemlja').val();
        const idGodOd=$('#idGodOd').val() === "" ? "null":$('#idGodOd').val();
        const idGodDo=$('#idGodDo').val() === "" ? "null":$('#idGodDo').val();
        const sort=$('#idSortFilmovi').val() === "" ? "null":$('#idSortFilmovi').val();

        fetch('http://localhost:8080/bioskop/filmovi/'+naziv+"/"+zanr+"/"+idOd+"/"+idDo+"/"+dist+"/"+zemlja+"/"+idGodOd+"/"+idGodDo+"/"+sort)
            .then(res => res.json())
            .then(res => {
                this.setState({
                    filmovi: res
                })
            })
    }

    handleChange(event){
        const {name, value} = event.target
        console.log("Name: "+name)
    }

    handleClick(event){
        const id=event.target.name
        this.state.filmovi.map(f => {
            if(f.id == id){
                this.setState({film:f})
            }
        })
        $('#tableFilmovi').hide();
    }

    deleteFilm(event){
        const id = event.target.name
        var config={
            method: 'delete',
          };

        fetch('http://localhost:8080/bioskop/filmovi/'+id,config)

        this.setState({
            user: ""
        })
        
        this.setState(prevState => {
            const updateFilmovi=prevState.filmovi.filter(u => u.id != id)
            return {
                filmovi: updateFilmovi
            }
        })
        
        window.location.reload();
    }

    editFilm(event){
        this.setState({
            user: ""
        })
        const id=event.target.name
        this.state.filmovi.map(f => {
            if(f.id == id){
                this.setState({editFilm:f})
            }
        })
        $('#idFilm').hide()
    }

    render(){
        const prikaz=this.state.filmovi.map(f => <PrikazFilmova handleClick={this.handleClick} key={f.id} film={f} />)
        const prikazFilma=this.state.film !== "" && <Film editFilm={this.editFilm} deleteFilm={this.deleteFilm} film={this.state.film} />
        const editFilma=this.state.editFilm !== "" && <EditFilm handleChange={this.handleChange} film={this.state.editFilm} />
        return(
            <div id="Filmovi">
                <table id="tableFilmovi" border="1">
                    <FilterFilmova filter={this.filter} />
                    {prikaz}
                </table>
                {prikazFilma}
                {editFilma}
            </div>
        )
    }

}

export default Filmovi
