import React from "react"

import Filmovi from "./Filmovi"
import NoviFilm from "./NoviFilm"
import Users from "./Users"
import Registracija from "./Registracija"
import Login from "./Login"
import Buttons from "./Buttons"

import $ from "jquery" 
import _decodeJWT from 'jwt-decode'

class Bioskop extends React.Component{
    constructor(){
        super()
        this.state = {
            usernameRequest:"",
            passwordRequest:"",
            logedUsername:"",
            roles:""
        }
        this.handleClick=this.handleClick.bind(this)
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }
    
    componentDidMount(){
        $('#main').hide()
    }

    handleClick(event){
        $('#main').children().hide();
        const name = event.target.name
      /*  $('#Filmovi').hide()
        $('#noviFilm').hide()
        $('#NoviFilm').show()*/
        if(name === "Filmovi"){
            window.location.reload()
        }else if(name === "logout"){
            localStorage.removeItem('jwt');
            this.setState({
                logedUsername:"",
                roles:""
            })
            console.log(JSON.stringify(this.state))
        }else{
            $('#'+name).show()
        }
    }

    handleChange(event){
        const {name,value} = event.target
        this.setState({[name]: value})
    }

    handleSubmit(e){
        e.preventDefault();
        const userLogin={
            username:this.state.usernameRequest,
            password:this.state.passwordRequest
        }
        var config={
            dataType: 'json',
            method: 'post',
            body: JSON.stringify(userLogin)
          };

        fetch('http://localhost:8080/auth/login',config)
          .then(res => res.json())
          .then(res => {
            const jwt = res.accessToken;
            localStorage.setItem('jwt', jwt);
            const decodedJWTData = _decodeJWT(jwt);
	    	if (decodedJWTData != null) {
                this.setState({
                    logedUsername:decodedJWTData.sub,
                    roles:decodedJWTData.roles,
                    passwordRequest:"",
                    usernameRequest:""
                })
	    	}
          })
        $('#idLogin').hide()
        
    }

    render(){
        const noviFilm=this.state.roles === "ROLE_ADMIN " && <NoviFilm />
        const users=this.state.roles === "ROLE_ADMIN " && <Users />
        const buttons=this.state.logedUsername !== "" &&  <Buttons state={this.state} handleClick={this.handleClick}/>
        return(
        <div id="Bioskop">
            <Login handleSubmit={this.handleSubmit} handleChange={this.handleChange} state={this.state}/>
            {buttons}
            <br/>
            <div id="main">
                <Filmovi />
                {noviFilm}
                <Registracija/>
            </div>
        </div>
    )}
}

export default Bioskop
