import React, {Component} from "react"

import FilterFilmova from "./FilterFilmova"
import PrikazFilmova from "./PrikazFilmova"

import $ from "jquery"

class Filmovi extends Component{
    
    constructor(){
        super()
        this.state = {
            filmovi: []
        }
        this.filter=this.filter.bind(this)
    }
    
    componentDidMount(){
        fetch('http://localhost:8080/bioskop/filmovi')
            .then(res => res.json())
            .then(response => {
                this.setState({
                    filmovi: response
            })
        })
    }

    filter(){
        console.log("U filteru sam")
        const naziv=$('#idNaziv').val() === "" ? "null":$('#idNaziv').val();

        fetch('http://localhost:8080/bioskop/filmovi/'+naziv)
            .then(res => res.json())
            .then(res => {
                this.setState({
                    filmovi: res
                })
            })
    }

    render(){
        const prikaz=this.state.filmovi.map(f => <PrikazFilmova key={f.id} film={f} />)
        return(
            <div id="Filmovi">
                <table border="1">
                    <FilterFilmova filter={this.filter} />
                    {prikaz}
                </table>
                <br/>
            </div>
        )
    }

}

export default Filmovi
