import React from "react"

import PrikazKorisnika from "./PrikazKorisnika"

import $ from "jquery"

class Users extends React.Component{

    constructor(){
        super()
        this.state = {
            users: []
        }
        this.handleChange=this.handleChange.bind(this)
        this.filter=this.filter.bind(this);
    }

    componentDidMount(){
        fetch('http://localhost:8080/bioskop/users')
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState(prevState => {
            const updateUsers=prevState.users.map(u => {
                if(u.korisnickoIme === name){
                    u.uloga=value
                    var config={
                        dataType: 'json',
                        method: 'put',
                        body: JSON.stringify(u)
                      };
            
                    // On submit of the form, send a POST request with the data to the server.
                    fetch('http://localhost:8080/bioskop/users',config)
                        .then(res => res.json())
                        .then(res => console.log(res))
                }
                return u;
            })
            return {
                users: updateUsers
            }
        })
    }

    filter(){
        const username=$('#idUsername').val() === "" ? "null":$('#idUsername').val();
        const role=$('#idRole').val() === "" ? "null":$('#idRole').val();

        console.log(username+" "+role)
        /*fetch('http://localhost:8080/bioskop/users/'+username+"/"+role)
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })*/
    }

    render(){
        const prikaz = this.state.users.map(u => <PrikazKorisnika handleChange={this.handleChange} key={u.korisnickoIme} user={u} />)
        return(
            <div id="Users">
                <table border="1">
                    <thead>
                        <tr>
                            <td>
                                <input 
                                    id="idUsername"
                                    type="text" 
                                    placeholder="Korisnicko ime..." 
                                    onChange={this.filter}/></td>
                            <td></td>
                            <td>
                                <select name="idRole" onChange={this.filter}>
                                    <option value="">--Izaberite ulogu za pretragu--</option>
                                    <option value="user">user</option>
                                    <option value="admin">admin</option>
                                </select>
                            </td>
                        </tr>
                        <tr>
                            <th>Korisnicko ime</th>
                            <th>Datum registracije</th>
                            <th>Uloga</th>
                        </tr>
                    </thead>
                    {prikaz}
                </table>
                <h1>{this.state.username} {this.state.role}</h1>
            </div>
        )
    }
}

export default Users
