import React from "react"

import Filmovi from "./Filmovi"
import NoviFilm from "./NoviFilm"
import Users from "./Users"
import Registracija from "./Registracija"
import Login from "./Login"

import $ from "jquery" 

class Bioskop extends React.Component{
    constructor(){
        super()
        this.state = {
            username:"",
            password:"",
            role:""
        }
        this.handleClick=this.handleClick.bind(this)
        this.handleChange=this.handleChange.bind(this)
    }
    
    componentDidMount(){
        $('#buttons').hide()
        $('#main').hide()
    }

    handleClick(event){
        $('#main').children().hide();
        const name = event.target.name
      /*  $('#Filmovi').hide()
        $('#noviFilm').hide()
        $('#NoviFilm').show()*/
        if(name === "Filmovi"){
            window.location.reload()
        }else{
            $('#'+name).show()
        }
    }

    handleChange(event){
        const  {name,value} = event.target
        this.setState({[name]:value})
    }

    render(){
        return(
        <div id="Bioskop">
            <Login handleChange={this.handleChange} state={this.state}/>
            <div id="buttons">
                <button id="noviFilm" name="NoviFilm" onClick={this.handleClick}>Novi film</button>
                <button id="sviKorisnici" name="Users" onClick={this.handleClick}>Svi korisnici</button>
                <button id="registracija" name="Registracija" onClick={this.handleClick}>Registracija</button>
                <button id="btnFilmovi" name="Filmovi" onClick={this.handleClick}>Svi filmovi</button>
            </div>
            <br/>
            <div id="main">
                <Filmovi />
                <NoviFilm />
                <Users />
                <Registracija/>
            </div>
        </div>
    )}
}

export default Bioskop
