import React from "react"

import Filmovi from "./Filmovi"
import NoviFilm from "./NoviFilm"
import Users from "./Users"

import $ from "jquery" 

class Bioskop extends React.Component{
    constructor(){
        super()
        this.handleClick=this.handleClick.bind(this)
    }
    
    componentDidMount(){
        $('#NoviFilm').hide()
        $('#Users').hide()
    }

    handleClick(event){
        $('#Bioskop').children().hide();
        const name = event.target.name
      /*  $('#Filmovi').hide()
        $('#noviFilm').hide()
        $('#NoviFilm').show()*/
        $('#'+name).show()

    }

    render(){
        return(
        <div id="Bioskop">
            <Filmovi />
            <button id="noviFilm" name="NoviFilm" onClick={this.handleClick}>Novi film</button>
            <NoviFilm />
            <Users />
            <button id="allUsers" name="Users" onClick={this.handleClick}>Svi korisnici</button>
        </div>
    )}
}

export default Bioskop
