import React, {Component} from "react"
import PrikazFilmova from "./PrikazFilmova"
import $ from "jquery"

class Filmovi extends Component{
    
    constructor(){
        super()
        this.state = {
            filmovi: []
        }
        this.handleClick=this.handleClick.bind(this)
    }
    
    componentDidMount(){
        let headers=new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');
        headers.append("Origin","http://localhost:8080/bioskop/filmovi")

        const config={
            headers: headers
        }

        fetch('http://localhost:8080/bioskop/filmovi')
            .then(res => res.json())
            .then(response => {
                this.setState({
                    filmovi: response
            })
        })
    }

    handleClick(){
        $('tabelaFilmovi').hide()
    }

    render(){
        const prikaz=this.state.filmovi.map(f => <PrikazFilmova key={f.id} film={f} />)
        return(
            <div>
                <table id="tabelaFilmovi" border="1">
                    <thead>
                        <tr>
                            <th>
                                Naziv
                            </th>
                            <th>
                                Zanr
                            </th>
                            <th>
                                Trajanje
                            </th>
                            <th>
                                Distributer
                            </th>
                            <th>
                                Zemlja porekla
                            </th>
                            <th>
                                Godina proizvodnje
                            </th>
                        </tr>
                    </thead>
                    {prikaz}
                </table>
                <br/>
                <button onClick={this.handleClick}>Dodaj novi film</button>
            </div>
        )
    }

}

export default Filmovi
