import React from "react"

import NovaProjekcijaForm from "./NovaProjekcijaForm"

class NovaProjekcija extends React.Component{
    constructor(){
        super()
        this.state = {
            film:"",
            datumVremePrikazivanja:"",
            tipProjekcije:"",
            sala:"",
            cenaKarte:"",
            sale:[]
        }
        this.handleSubmit=this.handleSubmit.bind(this)
        this.handleChange=this.handleChange.bind(this)
    }

    handleSubmit(){
        const projekcija={
            film:this.state.film,
            datumVremePrikazivanja:this.state.datumVremePrikazivanja,
            tipProjekcije:this.state.tipProjekcije,
            sala:this.state.sala,
            cenaKarte:this.state.cenaKarte,
            napravio: this.props.state.logedUsername
        }

        var config={
            dataType: 'json',
            contentType:"application/json; charset=utf-8",
            method: 'post',
            headers: {'Authorization': 'Bearer ' + localStorage.getItem('jwt')},
            body: JSON.stringify(this.state)
          };

        // On submit of the form, send a POST request with the data to the server.
        fetch('http://localhost:8080/bioskop/projekcije/add',config)
            .then(res => res.json())
            .then(res => this.props.handleToUpdateAdd(res))
    }

    componentDidMount(){

        const conf={
            headers: new Headers({'Authorization': 'Bearer ' + localStorage.getItem('jwt')},
                                  {'Access-Control-Allow-Methods':'GET'}),
          };
        fetch('http://localhost:8080/bioskop/sale',conf)
            .then(res => res.json())
            .then(response => {
                this.setState({
                    sale: response
            })
        })
    }

    handleChange(event){
        const {name,value} = event.target
        console.log(name+" "+JSON.stringify(value))
        if(name === "film"){
            this.props.state.filmovi.map(f => {
                if(f.id == value){
                    this.setState({film:f})
                }
            })
        }else if(name === "sala"){
            this.state.sale.map(s => {
                if(s.id == value){
                    this.setState({sala:s})
                }
            })
        }else if(name === "datumVremePrikazivanja"){
            const curentDateTime=new Date()
            console.log("curentDateTime: "+curentDateTime)
            const rezervacjija=value.json()
            if(curentDateTime>rezervacjija){
                alert("Vreme prikazivanje projekcije ne sme biti u proslosti!")
            }else{
                this.setState({
                    [name]:value
                })
            }
        }else{
            console.log("U else sam!")
            this.setState({
                [name]:value
            })
        }
    }

    render(){
        return(
            <div id="NovaProjekcija">
                <NovaProjekcijaForm state={this.state} handleChange={this.handleChange} filmovi={this.props.state.filmovi} handleSubmit={this.handleSubmit}/>
                <h1>{this.state.film.naziv} {this.state.tipProjekcije} {this.state.sala.naziv}
                 {this.state.datumVremePrikazivanja} {this.state.cenaKarte} {this.state.napravio}</h1>
            </div>
        )
    }
}

export default NovaProjekcija
