import React from "react"

import PrikazKorisnika from "./PrikazKorisnika"

class Users extends React.Component{

    constructor(){
        super()
        this.state = {
            users: []
        }
        this.handleChange=this.handleChange.bind(this)
    }

    componentDidMount(){
        fetch('http://localhost:8080/bioskop/users')
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState(prevState => {
            const updateUser=prevState.users.map(u => {
                if(u.korisnickoIme === name){
                    u.korisnickoIme=value
                }
                return u;
            })
            updateUser.uloga=value
            return {updateUser}
        })
        /*this.state.users.map(u => u.korisnickoIme == name && u => {
            let uloga=Object.assign({}, u.uloga)
            uloga=value
            return {uloga}
        })*/
    }

    render(){
        const prikaz = this.state.users.map(u => <PrikazKorisnika handleChange={this.handleChange} key={u.korisnickoIme} user={u} />)
        return(
            <div id="Users">
                <table border="1">
                    <thead>
                        <tr>
                            <th>Korisnicko ime</th>
                            <th>Datum registracije</th>
                            <th>Uloga</th>
                        </tr>
                    </thead>
                    {prikaz}
                </table>
            </div>
        )
    }
}

export default Users
