import React from "react"
import $ from "jquery"

import FilmForm from "./FilmForm"

class NoviFilm extends React.Component{
    constructor(){
        super()
        this.state = {
            naziv: "",
            reziser: "",
            glumci: "",
            zanrovi: "",
            trajanje: "",
            distributer: "",
            zemljaPorekla: "",
            godinaProizvodnje: "",
            opis: "",
            filmovi:[]
        }
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    componentDidMount(){
        this.setState({
            filmovi:this.props.filmovi
        })
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState({[name]:value})
    } 

    handleSubmit(e){
        e.preventDefault();

        var config={
            dataType: 'json',
            contentType:"application/json; charset=utf-8",
            method: 'post',
            headers: {'Authorization': 'Bearer ' + localStorage.getItem('jwt')},
            body: JSON.stringify(this.state)
          };

        // On submit of the form, send a POST request with the data to the server.
        fetch('http://localhost:8080/bioskop/filmovi/add',config)
            .then(res => res.json())
            .then(res => {
                this.setState(prevState => {
                   const updateFilmovi=prevState.filmovi.push(res)
            return {
                filmovi: updateFilmovi
            }
                })
            })
            
            this.props.handleToUpdate(filmovi)
            $('#Filmovi').show();
            $('#NoviFilm').hide();
      }

    render(){
        return(
            <div id="NoviFilm">
                <FilmForm handleChange={this.handleChange} handleSubmit={this.handleSubmit} state={this.state}/>
            </div>
        )
    }

}

export default NoviFilm
