import React from "react"

import PrikazKorisnika from "./PrikazSvihKorisnika"
import FilterUsers from "./FilterUsers"
import Korisnik from "./Korisnik"

import $ from "jquery"

class Users extends React.Component{

    constructor(){
        super()
        this.state = {
            users: []
        }
        this.handleChange=this.handleChange.bind(this)
        this.filter=this.filter.bind(this);
        this.handleClick=this.handleClick.bind(this);
    }

    componentDidMount(){
        fetch('http://localhost:8080/bioskop/users')
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState(prevState => {
            const updateUsers=prevState.users.map(u => {
                if(u.korisnickoIme === name){
                    u.uloga=value
                    var config={
                        dataType: 'json',
                        method: 'put',
                        body: JSON.stringify(u)
                      };
            
                    // On submit of the form, send a POST request with the data to the server.
                    fetch('http://localhost:8080/bioskop/users',config)
                        .then(res => res.json())
                        .then(res => console.log(res))
                }
                return u;
            })
            return {
                users: updateUsers
            }
        })
    }

    filter(){
        const username=$('#idUsername').val() === "" ? "null":$('#idUsername').val();
        const role=$('#idRole').val() === "" ? "null":$('#idRole').val();
        const sort=$('#idSort').val() === "" ? "null":$('#idSort').val();

        fetch('http://localhost:8080/bioskop/users/'+username+"/"+role+"/"+sort)
            .then(res => res.json())
            .then(res => {
                this.setState({
                    users: res
                })
            })
    }

    handleClick(event){
        const name=event.target.name
        const user=this.state.users.map(u => u.korisnickoIme === name && u)[0]
        
        $('#Bioskop').children().hide();
        $('#Bioskop').append('<Korisnik user=user />')
    }

    render(){
        const prikaz = this.state.users.map(u => <PrikazKorisnika handleClick={this.handleClick} handleChange={this.handleChange} key={u.korisnickoIme} user={u} />)
        return(
            <div id="Users">
                <table border="1">
                    <FilterUsers filter={this.filter} />
                    <tbody>
                    {prikaz}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Users
